package com.syntax.class07;

public class WhileLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int time= 1;
if (time<12) {
	System.out.println("Morning");// the result will print one time if its true
}while(time<12) {
	System.out.println("Morning");
	time++;}
	// the result will print infinite
	// to stop the loop we have to add countable variable by adding time++
	System.out.println("Hello");
	
	
	
	
}
	}


